// my_server.js

// Simple HTTP server

//import HTTP
var http = require("http"),
	fs = require("fs");

var app = http.createServer(function(req, res){

	var url = req.url,
	method = req.method.toLowerCase();

	fs.readFile("../../../resources/tournament.json" , "utf-8" , function(err , data){

		if(err){
			console.log(err);
			return;
		}

		res.writeHead(200 , {"Content-Type" : "text/plain"});
		res.end(data);

	});
	

}).listen(8080);